    private void entrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_entrarActionPerformed
        MenuFuncionario menuF = new MenuFuncionario(c, menuP);
        
        if(user.getText().equals("Bob") && new String(senha.getPassword()).equals("marley"))
        {
            this.setVisible(false);
            menuF.setSize(300, 400);
            menuF.setLocationRelativeTo(null);
            menuF.setVisible(true);
        }
        //else Gerente
        else
            JOptionPane.showMessageDialog(this, "Login ou senha incorretos. Tente novamente!", "Error!", JOptionPane.ERROR_MESSAGE);
    }//GEN-LAST:event_entrarActionPerformed

